if not data:
    sys.exit(0)
ptr = 0
out_lines = []
while ptr < len(data):
    n = data[ptr]; k = data[ptr+1]; ptr += 2
    adj = [[] for _ in range(n+1)]
    for _ in range(n-1):
        a = data[ptr]; b = data[ptr+1]; ptr += 2
        adj[a].append(b)
        adj[b].append(a)
    root = n
    parent = [0]*(n+1)
    parent[root] = 0
    stack = [root]
    while stack:
        u = stack.pop()
        for v in adj[u]:
            if v != parent[u]:
                parent[v] = u
                stack.append(v)
    used = [False]*(n+1)
    remaining = n - k
    for v in range(n, 0, -1):
        if used[v]:
            continue
        path = []
        u = v
        while True:
            if used[u]:
                break
            path.append(u)
            if parent[u] == 0:
                break
            u = parent[u]
        if len(path) <= remaining:
            for x in path:
                used[x] = True
            remaining -= len(path)
        if remaining == 0:
            break
    removed = [str(i) for i in range(1, n+1) if not used[i]]
    out_lines.append(" ".join(removed))
sys.stdout.write("\n".join(out_lines))

