import sys

data = sys.stdin.read().strip().splitlines()
if not data:
    sys.exit(0)

def solve_line(line):
    parts = line.strip().split()
    if len(parts) != 3:
        return ""
    tiles = parts
    # parse into (num,suit)
    cells = []
    for t in tiles:
        if len(t) != 2:
            return ""
        n = int(t[0])
        s = t[1]
        cells.append((n, s))
    # if any koutsu or shuntsu exists => 0
    # check koutsu
    if cells[0]==cells[1]==cells[2]:
        return "0"
    # check shuntsu: same suit and numbers form consecutive triple in any order
    suits = [s for (_,s) in cells]
    nums = [n for (n,_) in cells]
    if suits[0]==suits[1]==suits[2]:
        nums_sorted = sorted(nums)
        if nums_sorted[0]+1==nums_sorted[1] and nums_sorted[1]+1==nums_sorted[2]:
            return "0"
    # check if one tile needed: if any pair can form part of koutsu (two identical) or part of shuntsu (same suit and numbers within 2)
    # if any two identical -> 1 (draw third)
    for i in range(3):
        for j in range(i+1,3):
            if cells[i]==cells[j]:
                return "1"
    # check pairs for shuntsu possibility
    for i in range(3):
        for j in range(i+1,3):
            ni, si = cells[i]
            nj, sj = cells[j]
            if si!=sj:
                continue
            d = abs(ni-nj)
            if d<=2:
                return "1"
    # else need 2
    return "2"

out_lines = []
for line in data:
    line = line.strip()
    if not line:
        continue
    out_lines.append(solve_line(line))

sys.stdout.write("\n".join(out_lines))

