import sys

data = list(map(int, sys.stdin.read().split()))
if not data:
    sys.exit(0)
cases = []
if len(data) % 4 == 0:
    for i in range(0, len(data), 4):
        cases.append(tuple(data[i:i+4]))
else:
    cases.append(tuple(data[:4]))

out_lines = []
for n, pos, l, r in cases:
    if l == 1 and r == n:
        out_lines.append("0")
        continue
    ans = 10**9
    if pos < l:
        cost = (l - pos) + 1
        if r < n:
            cost += (r - l) + 1
        ans = min(ans, cost)
    elif pos > r:
        cost = (pos - r) + 1
        if l > 1:
            cost += (r - l) + 1
        ans = min(ans, cost)
    else:
        if l > 1 and r < n:
            ans = min(ans, min(pos - l, r - pos) + (r - l) + 2)
        elif l > 1:
            ans = min(ans, pos - l + 1)
        elif r < n:
            ans = min(ans, r - pos + 1)
    out_lines.append(str(ans))

sys.stdout.write("\n".join(out_lines))

