import sys

data = list(map(int, sys.stdin.read().strip().split()))
if len(data) < 4:
    sys.exit(0)
A, B, C, D = data[0], data[1], data[2], data[3]

lenX = B - A + 1
Smax = D - B + 1
if Smax < 1:
    print(0)
    sys.exit(0)

P = [0] * (Smax + 1)
for s in range(1, Smax + 1):
    if s <= A:
        g = lenX
    elif s <= B:
        g = B - s + 1
    else:
        g = 0
    P[s] = P[s-1] + g

ans = 0
for y in range(B, C + 1):
    L = C - y + 1
    R = D - y + 1
    if L <= R:
        ans += P[R] - P[L-1]
print(ans)
