data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)
outs = []
if len(data) < 4:
    sys.exit(0)
cases = []
if len(data) == 4:
    cases.append(tuple(data))
else:
    # if more than 4, treat input as multiple test cases of 4 ints each
    for i in range(0, len(data), 4):
        if i+3 < len(data):
            cases.append((data[i], data[i+1], data[i+2], data[i+3]))

for A, B, C, D in cases:
    M = D - C + 1
    total = 0
    for y in range(B, C+1):
        x0 = max(A, C - y + 1)
        if x0 > B:
            continue
        n = B - x0 + 1
        s = x0 + y - C
        if s >= M:
            total += n * M
        else:
            p = min(n, M - s)
            total += p * s + p * (p - 1) // 2
            total += (n - p) * M
    outs.append(str(total))

sys.stdout.write("\n".join(outs))

