import sys

MOD = 1000000007

def modinv(x):
    return pow(x, MOD-2, MOD)

def prepare_fact(n):
    fact = [1]*(n+1)
    for i in range(1,n+1):
        fact[i] = fact[i-1]*i % MOD
    invfact = [1]*(n+1)
    invfact[n] = modinv(fact[n])
    for i in range(n,0,-1):
        invfact[i-1] = invfact[i]*i % MOD
    return fact, invfact

def comb(n, k, fact, invfact):
    if k<0 or k>n or n<0:
        return 0
    return fact[n]*invfact[k]%MOD*invfact[n-k]%MOD

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    n = int(data[0]); m = int(data[1]); g = int(data[2])
    total_len = n + m
    fact, invfact = prepare_fact(total_len)
    # base count1 for n==0
    if n == 0:
        count1 = 1 if m == 1 else 0
    else:
        # alternating sum S = sum_{i=0..n-1} (-1)^i C(n+m-1-i, n-1-i)
        S = 0
        sign = 1
        for i in range(0, n):
            a = comb(n+m-1-i, n-1-i, fact, invfact)
            if sign == 1:
                S = (S + a) % MOD
            else:
                S = (S - a) % MOD
            sign = -sign
        base0 = 1 if (m == 1) else 0  # count1(0,m)
        if (n % 2) == 1:
            S = (S - base0) % MOD
        else:
            S = (S + base0) % MOD
        count1 = S % MOD
    total = comb(n+m, n, fact, invfact)
    if g == 1:
        ans = count1
    else:
        ans = (total - count1) % MOD
    print(ans)

if __name__ == "__main__":
    main()
