import sys

MOD = 1000000007

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
vals = list(map(int, data))
triples = []
i = 0
while i + 2 < len(vals):
    triples.append((vals[i], vals[i+1], vals[i+2]))
    i += 3

maxN = 0
for n,m,g in triples:
    maxN = max(maxN, n + m)

fact = [1] * (maxN + 1)
for i in range(1, maxN + 1):
    fact[i] = fact[i-1] * i % MOD
invfact = [1] * (maxN + 1)
invfact[maxN] = pow(fact[maxN], MOD-2, MOD)
for i in range(maxN, 0, -1):
    invfact[i-1] = invfact[i] * i % MOD

def nCr(n, r):
    if r < 0 or r > n:
        return 0
    return fact[n] * invfact[r] % MOD * invfact[n-r] % MOD

out_lines = []
for n,m,g in triples:
    if m == 0:
        final = (n-1) % 2
        out_lines.append("1" if final == g else "0")
        continue
    ans = 0
    for a in range(0, n+1):
        ways = nCr(n + m - a - 1, n - a)
        if a == n and m == 1:
            final = 1 ^ (n % 2)
        else:
            final = a % 2
        if final == g:
            ans = (ans + ways) % MOD
    out_lines.append(str(ans))

sys.stdout.write("\n".join(out_lines))

