import sys

MOD = 1000000007

def prepare_factorials(nmax):
    fact = [1] * (nmax + 1)
    invfact = [1] * (nmax + 1)
    for i in range(1, nmax + 1):
        fact[i] = fact[i-1] * i % MOD
    invfact[nmax] = pow(fact[nmax], MOD-2, MOD)
    for i in range(nmax, 0, -1):
        invfact[i-1] = invfact[i] * i % MOD
    return fact, invfact

def comb(n, k, fact, invfact):
    if k < 0 or k > n or n < 0:
        return 0
    return fact[n] * invfact[k] % MOD * invfact[n-k] % MOD

def compute_F(n, m, fact, invfact):
    L = n + m
    if L == 1:
        # single character
        return 1 if n == 1 else 0
    # sum of terms C(n+m-1 - 2j, n-2j) for j=0..k-1, where k = floor(n/2)
    k = n // 2
    total = 0
    for j in range(k):
        a = n + m - 1 - 2*j
        b = n - 2*j
        total = (total + comb(a, b, fact, invfact)) % MOD
    r = n - 2*k  # r is 0 or 1
    # base F(r, m)
    if r == 0:
        # F(0, m): if m >= 2 -> 1, if m==1 or m==0 -> 0
        if m >= 2:
            base = 1
        else:
            base = 0
    else:
        # r == 1
        if m == 0:
            base = 1
        else:
            base = (m + (1 if m == 1 else 0)) % MOD
    total = (total + base) % MOD
    return total

def process_case(n, m, g, fact, invfact):
    L = n + m
    total = comb(n+m, n, fact, invfact)
    F = compute_F(n, m, fact, invfact)
    if g == 0:
        return F % MOD
    else:
        return (total - F) % MOD

def main():
    data = sys.stdin.read().strip().splitlines()
    if not data:
        return
    cases = []
    for line in data:
        line = line.strip()
        if not line:
            continue
        parts = line.split()
        if len(parts) < 3:
            continue
        try:
            n = int(parts[0]); m = int(parts[1]); g = int(parts[2])
        except:
            continue
        cases.append((n,m,g))
    if not cases:
        return
    maxN = max(n+m for (n,m,_) in cases)
    fact, invfact = prepare_factorials(maxN)
    out_lines = []
    for n,m,g in cases:
        out_lines.append(str(process_case(n,m,g,fact,invfact)))
    sys.stdout.write("\n".join(out_lines))

if __name__ == "__main__":
    main()
