import sys

MOD = 1000000007

def modinv(a):
    return pow(a, MOD-2, MOD)

def prepare_fact(n):
    fact = [1]*(n+1)
    for i in range(1,n+1):
        fact[i] = fact[i-1]*i%MOD
    invfact = [1]*(n+1)
    invfact[n] = modinv(fact[n])
    for i in range(n,0,-1):
        invfact[i-1] = invfact[i]*i%MOD
    return fact, invfact

def comb(n,k,fact,invfact):
    if k<0 or k>n: return 0
    return fact[n]*invfact[k]%MOD*invfact[n-k]%MOD

def solve_case(n,m,g):
    # handle trivial lengths
    if n==0 and m==0:
        return 0
    # final single character obtainable: compute parity?
    # Known solution: sequences reducible to g iff (m%2==g)
    # But need count of initial strings with n zeros and m ones that reduce to g.
    # This CF problem reduces to counting placements of ones that form blocks.
    # Use combinatorics: if g==1 then m%2==1 required; if g==0 then m%2==0 required.
    if (m%2) != g:
        return 0
    # We need number of sequences of length n+m with given counts that reduce to g.
    # Equivalent to count of sequences where number of ones after full reductions equals g (1 or 0).
    # Known formula: answer = C(n+m-1, n) if g==0 else C(n+m-1, n-1)
    # Handle edge cases when n==0 or m==0 appropriately.
    total = n+m
    maxN = total
    fact,invfact = prepare_fact(maxN)
    if g==0:
        # when n==0, only possible if m%2==0 and resulting string 0? but with no zeros initial cannot get 0 unless m leads to 0. formula works when n>0; handle n==0
        if total==1:
            return 1 if g==m%2 else 0
        return comb(total-1, n, fact, invfact)
    else:
        # g==1
        if n==0:
            return 1 if m==1 else 0
        return comb(total-1, n-1, fact, invfact)

def main():
    data = sys.stdin.read().strip().split()
    if not data: 
        return
    n = int(data[0]); m = int(data[1]); g = int(data[2])
    print(solve_case(n,m,g)%MOD)

if __name__ == "__main__":
    main()
