diag_char = grid[0][0]
off_char = None
ok = True
for i in range(n):
    for j in range(n):
        if i == j or i + j == n - 1:
            if grid[i][j] != diag_char:
                ok = False
                break
        else:
            if off_char is None:
                off_char = grid[i][j]
            if grid[i][j] != off_char:
                ok = False
                break
    if not ok:
        break

if off_char == diag_char:
    ok = False

print("YES" if ok else "NO")

