    if not data:
        return
    n = int(data[0])
    A = data[1].strip()
    digits = [int(c) for c in A]
    # DP over counts of Homer's picks (i) and Marge's picks (j)
    INF_NEG = -10**100
    dp = [[INF_NEG] * (n+1) for _ in range(n+1)]
    prev = [[None] * (n+1) for _ in range(n+1)]
    dp[0][0] = 0
    pow10 = [10**k for k in range(n)]  # 10^0 .. 10^{n-1}
    # We need weight 10^{n-1 - idx}
    for s in range(0, 2*n):
        for i in range(max(0, s-n), min(n, s)+1):
            j = s - i
            if j < 0 or j > n:
                continue
            if dp[i][j] == INF_NEG:
                continue
            d = digits[s]
            # Homer pick
            if i < n:
                w = pow10[n-1 - i]
                val = dp[i][j] + d * w
                if val > dp[i+1][j]:
                    dp[i+1][j] = val
                    prev[i+1][j] = 'H'
            # Marge pick
            if j < n:
                w = pow10[n-1 - j]
                val = dp[i][j] + d * w
                if val > dp[i][j+1]:
                    dp[i][j+1] = val
                    prev[i][j+1] = 'M'
    # Reconstruct
