import sys

def process_case(a_str, s_str):
    a = list(a_str.strip())
    s = sorted(s_str.strip(), reverse=True)
    j = 0
    m = len(s)
    for i in range(len(a)):
        if j < m and s[j] > a[i]:
            a[i] = s[j]
            j += 1
        if j >= m:
            break
    return ''.join(a)

def main():
    data = [line.rstrip('\n') for line in sys.stdin if line.strip() != '']
    out = []
    i = 0
    while i + 1 < len(data):
        a_str = data[i].strip()
        s_str = data[i+1].strip()
        out.append(process_case(a_str, s_str))
        i += 2
    # If odd number of non-empty lines, ignore the last incomplete one.
    sys.stdout.write("\n".join(out))

if __name__ == "__main__":
    main()

