import sys, math

data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)
out_lines = []
def C2(x):
    return x*(x-1)//2
for p in data:
    if p == 0:
        out_lines.append("0 0")
        continue
    k = (1 + math.isqrt(1 + 8*p)) // 2
    while C2(k) > p:
        k -= 1
    while C2(k+1) <= p:
        k += 1
    rem = p - C2(k)
    if rem == 0:
        out_lines.append(f"{k} 0")
        continue
    t = (1 + math.isqrt(1 + 8*rem)) // 2
    while C2(t) < rem:
        t += 1
    while C2(t-1) >= rem:
        t -= 1
    n = k + t
    unidir = k*t + C2(t) - rem
    out_lines.append(f"{n} {unidir}")
sys.stdout.write("\n".join(out_lines))

