import sys

def check_string(s: str) -> str:
    # must be of form a^i b^j c^k with i,j,k >= 1 and k == i or k == j
    n = len(s)
    if n == 0:
        return "NO"
    # count prefix a
    i = 0
    while i < n and s[i] == 'a':
        i += 1
    # must have at least one a
    if i == 0:
        return "NO"
    # count b's
    j = i
    while j < n and s[j] == 'b':
        j += 1
    # must have at least one b
    if j == i:
        return "NO"
    # rest must be c's
    k = j
    while k < n and s[k] == 'c':
        k += 1
    if k != n:
        return "NO"
    cnt_a = i
    cnt_b = j - i
    cnt_c = k - j
    if cnt_c == cnt_a or cnt_c == cnt_b:
        return "YES"
    return "NO"

