import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)

def check(s):
    n = len(s)
    # must be only a,b,c
    if any(ch not in 'abc' for ch in s):
        return False
    # find a segment
    i = 0
    while i < n and s[i] == 'a':
        i += 1
    a_cnt = i
    j = i
    while j < n and s[j] == 'b':
        j += 1
    b_cnt = j - i
    k = j
    while k < n and s[k] == 'c':
        k += 1
    c_cnt = k - j
    if k != n:
        return False
    if a_cnt <= 0 or b_cnt <= 0 or c_cnt <= 0:
        return False
    if c_cnt == a_cnt or c_cnt == b_cnt:
        return True
    return False

out = []
for s in data:
    out.append("YES" if check(s) else "NO")

sys.stdout.write("\n".join(out))

