import sys, ast

data = sys.stdin.read().strip()
if not data:
    sys.exit(0)

tests = None
data_strip = data.strip()
if data_strip and data_strip[0] in "[(":
    try:
        parsed = ast.literal_eval(data_strip)
        if isinstance(parsed, (list, tuple)):
            tests = [str(x) for x in parsed]
        elif isinstance(parsed, str):
            tests = [parsed]
    except Exception:
        tests = None

if tests is None:
    if '\n' in data:
        tests = [line.strip() for line in data.splitlines() if line.strip()]
    else:
        parts = data.split()
        tests = parts if len(parts) > 1 else [data.strip()]

out_lines = []
for s in tests:
    n = len(s)
    i = 0
    while i < n and s[i] == 'a':
        i += 1
    ca = i
    j = i
    while i < n and s[i] == 'b':
        i += 1
    cb = i - j
    k = i
    while i < n and s[i] == 'c':
        i += 1
    cc = i - k
    if i != n or ca == 0 or cb == 0 or cc == 0:
        out_lines.append("NO")
    else:
        out_lines.append("YES" if (cc == ca or cc == cb) else "NO")

sys.stdout.write("\n".join(out_lines))

