import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
n = int(next(it))
a = [int(next(it)) for _ in range(n)]

if n == 1:
    print(1)
    sys.exit(0)

L = [1]*n
for i in range(1, n):
    if a[i] > a[i-1]:
        L[i] = L[i-1] + 1
    else:
        L[i] = 1

R = [1]*n
for i in range(n-2, -1, -1):
    if a[i] < a[i+1]:
        R[i] = R[i+1] + 1
    else:
        R[i] = 1

ans = max(L)  # without any change

for i in range(n):
    left = L[i-1] if i-1 >= 0 else 0
    right = R[i+1] if i+1 < n else 0
    # change a[i] to fit between a[i-1] and a[i+1]
    if 0 < i < n-1 and a[i-1] + 1 < a[i+1]:
        ans = max(ans, left + 1 + right)
    # or extend by changing boundary element
    ans = max(ans, left + 1, right + 1)

# cap by n
if ans > n:
    ans = n

print(ans)

