import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
n = int(data[0])
a = list(map(int, data[1:1+n]))

if n == 1:
    print(1)
    sys.exit(0)

l = [1]*n
for i in range(1, n):
    if a[i] > a[i-1]:
        l[i] = l[i-1] + 1
r = [1]*n
for i in range(n-2, -1, -1):
    if a[i] < a[i+1]:
        r[i] = r[i+1] + 1

ans = max(l)
for i in range(n):
    if i == 0:
        ans = max(ans, 1 + r[1])
    elif i == n-1:
        ans = max(ans, l[n-2] + 1)
    else:
        if a[i+1] - a[i-1] >= 2:
            ans = max(ans, l[i-1] + 1 + r[i+1])
        ans = max(ans, l[i-1] + 1, r[i+1] + 1)

if ans > n:
    ans = n
print(ans)

