import sys

data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)
n = data[0]
a = data[1:]
if n == 0:
    print(0)
    sys.exit(0)
incL = [0] * n
incR = [0] * n
for i in range(n):
    if i == 0:
        incL[i] = 1
    else:
        if a[i] > a[i - 1]:
            incL[i] = incL[i - 1] + 1
        else:
            incL[i] = 1
for i in range(n - 1, -1, -1):
    if i == n - 1:
        incR[i] = 1
    else:
        if a[i] < a[i + 1]:
            incR[i] = incR[i + 1] + 1
        else:
            incR[i] = 1
ans = max(incL)
for i in range(n):
    left = incL[i - 1] if i > 0 else 0
    right = incR[i + 1] if i + 1 < n else 0
    if 0 < i < n - 1 and a[i + 1] - a[i - 1] >= 2:
        ans = max(ans, left + 1 + right)
    else:
        ans = max(ans, left + 1, 1 + right)
if ans > n:
    ans = n
print(ans)

