import sys

MOD = 998244353

def modpow(a, e):
    return pow(a, e, MOD)

def prepare_fact(n):
    fact = [1] * (n+1)
    for i in range(1, n+1):
        fact[i] = fact[i-1] * i % MOD
    invfact = [1] * (n+1)
    invfact[n] = pow(fact[n], MOD-2, MOD)
    for i in range(n, 0, -1):
        invfact[i-1] = invfact[i] * i % MOD
    return fact, invfact

def comb(n, k, fact, invfact):
    if k < 0 or k > n: return 0
    return fact[n] * invfact[k] % MOD * invfact[n-k] % MOD

data = sys.stdin.read().strip().split()
out = []
fact, invfact = prepare_fact(500000)  # max n
for token in data:
    n = int(token)
    if n % 2 == 1:
        out.append("0")
        continue
    if n == 0:
        out.append("0")
        continue
    total = 0
    # sum_{k=1..n-1} C(n-2,k-1) * (-1)^k * k^{k-1} * (n-k)^{n-k-1}
    for k in range(1, n):
        c = comb(n-2, k-1, fact, invfact)
        sign = -1 if (k % 2 == 1) else 1
        a = 1
        if k >= 1:
            a = modpow(k, k-1)
        b = 1
        nk = n-k
        if nk >= 1:
            b = modpow(nk, nk-1)
        term = c * a % MOD * b % MOD
        if sign == -1:
            total -= term
        else:
            total += term
        if total >= MOD: total -= MOD
        if total < 0: total += MOD
    out.append(str(total % MOD))

print("\n".join(out))

