import sys
MOD = 998244353

def modinv(x):
    return pow(x, MOD-2, MOD)

data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)
maxn = max(data)
# prepare factorials up to maxn
N = max(2, maxn)
fac = [1] * (N+1)
for i in range(1, N+1):
    fac[i] = fac[i-1] * i % MOD
invfac = [1] * (N+1)
invfac[N] = modinv(fac[N])
for i in range(N, 0, -1):
    invfac[i-1] = invfac[i] * i % MOD

out = []
for n in data:
    if n % 2 == 1:
        out.append("0")
        continue
    if n == 1:
        out.append("0")
        continue
    ans = 0
    # sum s=1..n-1 C(n-2, s-1) * (-1)^s * s^{s-1} * (n-s)^{n-s-1}
    for s in range(1, n):
        c = fac[n-2] * invfac[s-1] % MOD * invfac[n-s-1] % MOD
        a = pow(s, s-1, MOD)
        b = pow(n-s, n-s-1, MOD)
        term = c * a % MOD * b % MOD
        if s % 2 == 1:
            ans -= term
        else:
            ans += term
    ans %= MOD
    out.append(str(ans))
sys.stdout.write("\n".join(out))

