import sys

data = list(map(int, sys.stdin.read().strip().split()))
if not data:
    sys.exit(0)

def solve(a, b):
    candles = a
    rem = 0
    hours = 0
    while candles > 0:
        hours += candles
        rem += candles
        candles = rem // b
        rem = rem % b
    return hours

out = []
if len(data) == 2:
    out.append(str(solve(data[0], data[1])))
else:
    # process as pairs if multiple pairs provided
    for i in range(0, len(data), 2):
        if i+1 < len(data):
            out.append(str(solve(data[i], data[i+1])))

sys.stdout.write("\n".join(out))

