import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
nums = list(map(int, data))
pairs = []
if len(nums) == 2:
    pairs = [tuple(nums)]
else:
    # group into pairs
    for i in range(0, len(nums), 2):
        if i+1 < len(nums):
            pairs.append((nums[i], nums[i+1]))

out_lines = []
for a, b in pairs:
    total = a
    leftovers = a
    while leftovers >= b:
        new = leftovers // b
        total += new
        leftovers = new + (leftovers % b)
    out_lines.append(str(total))

sys.stdout.write("\n".join(out_lines))

