import sys

def solve_pair(a, b):
    hours = a
    rem = a
    while rem >= b:
        new = rem // b
        hours += new
        rem = new + (rem % b)
    return hours

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    nums = list(map(int, data))
    results = []
    if len(nums) == 2:
        results.append(str(solve_pair(nums[0], nums[1])))
    else:
        for i in range(0, len(nums) - 1, 2):
            results.append(str(solve_pair(nums[i], nums[i+1])))
    sys.stdout.write("\n".join(results))

if __name__ == "__main__":
    main()
