import sys

data = sys.stdin.read().strip().splitlines()
if not data:
    sys.exit(0)

# If input is like the example with list notation, handle that:
if len(data) == 1 and data[0].startswith('[') and data[0].endswith(']'):
    import ast
    try:
        arr = ast.literal_eval(data[0])
        if isinstance(arr, list):
            strings = arr
        else:
            strings = [data[0]]
    except Exception:
        strings = [data[0]]
else:
    # Each non-empty line is a separate test string
    strings = [line.strip() for line in data if line.strip() != '']

out = []
for s in strings:
    n = len(s)
    # binary search length L where some substring of length L appears at least twice
    def exists(L):
        seen = {}
        for i in range(n - L + 1):
            sub = s[i:i+L]
            if sub in seen:
                return True
            seen[sub] = True
        return False
    lo, hi = 0, n
    while lo < hi:
        mid = (lo + hi + 1) // 2
        if exists(mid):
            lo = mid
        else:
            hi = mid - 1
    out.append(str(lo))

sys.stdout.write("\n".join(out))

