s_all = sys.stdin.read().strip().splitlines()
if not s_all:
    sys.exit(0)

def longest_repeat(s):
    n = len(s)
    # check lengths from n-1 down to 1
    for L in range(n-1, 0, -1):
        seen = set()
        for i in range(n - L + 1):
            sub = s[i:i+L]
            if sub in seen:
                return L
            seen.add(sub)
    return 0

