import sys

data = [line.strip() for line in sys.stdin.readlines() if line.strip() != ""]
out_lines = []
i = 0
while i + 2 < len(data):
    n = int(data[i])
    l = data[i+1]
    r = data[i+2]
    i += 3

    left_pos = {chr(c): [] for c in range(ord('a'), ord('z')+1)}
    right_pos = {chr(c): [] for c in range(ord('a'), ord('z')+1)}
    left_q = []
    right_q = []

    for idx, ch in enumerate(l, start=1):
        if ch == '?':
            left_q.append(idx)
        else:
            left_pos[ch].append(idx)
    for idx, ch in enumerate(r, start=1):
        if ch == '?':
            right_q.append(idx)
        else:
            right_pos[ch].append(idx)

    pairs = []

    # match same letters
    for ch in left_pos:
        L = left_pos[ch]
        R = right_pos[ch]
        while L and R:
            pairs.append((L.pop(), R.pop()))

    # match left '?' with remaining right specific letters
    for ch in right_pos:
        R = right_pos[ch]
        while R and left_q:
            pairs.append((left_q.pop(), R.pop()))

    # match right '?' with remaining left specific letters
    for ch in left_pos:
        L = left_pos[ch]
        while L and right_q:
            pairs.append((L.pop(), right_q.pop()))

    # match remaining '?' with '?'
    while left_q and right_q:
        pairs.append((left_q.pop(), right_q.pop()))

    out_lines.append(str(len(pairs)))
    for a, b in pairs:
        out_lines.append(f"{a} {b}")

sys.stdout.write("\n".join(out_lines))

