import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
n = int(next(it))
l = list(next(it).strip())
r = list(next(it).strip())

from collections import defaultdict, deque

posL = defaultdict(deque)
posR = defaultdict(deque)
qL = deque()
qR = deque()

for i, ch in enumerate(l, start=1):
    if ch == '?':
        qL.append(i)
    else:
        posL[ch].append(i)
for i, ch in enumerate(r, start=1):
    if ch == '?':
        qR.append(i)
    else:
        posR[ch].append(i)

pairs = []

# match specific letters
for c in list(posL.keys()):
    while posL[c] and posR.get(c):
        if not posR[c]:
            break
        pairs.append((posL[c].popleft(), posR[c].popleft()))

# match remaining left specific with right '?'
for c in posL:
    while posL[c] and qR:
        pairs.append((posL[c].popleft(), qR.popleft()))

# match remaining right specific with left '?'
for c in posR:
    while posR[c] and qL:
        pairs.append((qL.popleft(), posR[c].popleft()))

# match remaining '?' with '?'
while qL and qR:
    pairs.append((qL.popleft(), qR.popleft()))

print(len(pairs))
out = []
for a,b in pairs:
    out.append(f"{a} {b}")
print("\n".join(out))

