import sys

def main():
    data = sys.stdin.read().strip().split()
    if not data:
        return
    it = iter(data)
    n = int(next(it))
    x = int(next(it))
    segs = []
    maxT = 0
    for _ in range(n):
        l = int(next(it)); r = int(next(it)); c = int(next(it))
        segs.append((l, r, c))
        if r > maxT: maxT = r
        if l > maxT: maxT = l
    # prepare lists by start and end
    starts_at = [[] for _ in range(maxT+2)]
    ends_at = [[] for _ in range(maxT+2)]
    for l, r, c in segs:
        length = r - l + 1
        if length <= x:  # only lengths <= x could be useful
            starts_at[l].append((length, r, c))
            ends_at[r].append((length, l, c))
    INF = 10**30
    best_len = [INF] * (x+1)  # best cost for each length among segments that ended before current l
    ans = INF
    # iterate l from 1..maxT
    for l in range(1, maxT+1):
        # add segments that end at l-1 into best_len
        eidx = l-1
        if eidx >= 0:
            for length, st, cost in ends_at[eidx]:
                if length <= x:
                    if cost < best_len[length]:
                        best_len[length] = cost
        # process segments starting at l
        for length, r, cost in starts_at[l]:
            need = x - length
            if need >= 1 and need <= x:
                bc = best_len[need]
                if bc < INF:
                    total = bc + cost
                    if total < ans:
                        ans = total
    if ans == INF:
        print(-1)
    else:
        print(ans)

if __name__ == "__main__":
    main()
