import sys

def solve_one(n, x, intervals):
    intervals_by_l = sorted(intervals, key=lambda t: t[0])
    intervals_by_r = sorted(intervals, key=lambda t: t[1])
    INF = 10**30
    max_len = x  # we only care about lengths up to x
    best_by_len = [INF] * (max_len + 1)
    ans = INF
    p = 0  # pointer in intervals_by_r
    for l, r, c in intervals_by_l:
        # add all intervals that end before current l
        while p < n and intervals_by_r[p][1] < l:
            rl, rr, rc = intervals_by_r[p]
            d = rr - rl + 1
            if d <= max_len:
                if rc < best_by_len[d]:
                    best_by_len[d] = rc
            p += 1
        cur_d = r - l + 1
        need = x - cur_d
        if 0 < need <= max_len:
            if best_by_len[need] < INF:
                total = c + best_by_len[need]
                if total < ans:
                    ans = total
    return -1 if ans == INF else ans

def main():
    data = list(map(int, sys.stdin.read().strip().split()))
    if not data:
        return
    it = iter(data)
    out_lines = []
    while True:
        try:
            n = next(it)
        except StopIteration:
            break
        x = next(it)
        intervals = []
        for _ in range(n):
            l = next(it); r = next(it); c = next(it)
            intervals.append((l, r, c))
        out_lines.append(str(solve_one(n, x, intervals)))
    sys.stdout.write("\n".join(out_lines))

if __name__ == "__main__":
    main()

