import sys
from bisect import bisect_left, bisect_right

data = sys.stdin.read().strip().split()
it = iter(data)
try:
    n = int(next(it))
except StopIteration:
    sys.exit(0)
x = int(next(it))

segs = []
for _ in range(n):
    l = int(next(it)); r = int(next(it)); c = int(next(it))
    segs.append((l, r, c))

ends_by_len = {}
starts_by_len = {}

for l, r, c in segs:
    length = r - l + 1
    ends_by_len.setdefault(length, []).append((r, c))
    starts_by_len.setdefault(length, []).append((l, c))

# prepare sorted arrays with prefix/suffix minima
ends_pre = {}
for length, lst in ends_by_len.items():
    lst.sort()  # sort by r
    rs = [rc[0] for rc in lst]
    pref = []
    cur = 10**30
    for _, cost in lst:
        if cost < cur:
            cur = cost
        pref.append(cur)
    ends_pre[length] = (rs, pref)

starts_suf = {}
for length, lst in starts_by_len.items():
    lst.sort()  # sort by l ascending
    ls = [lc[0] for lc in lst]
    suf = [0]*len(lst)
    cur = 10**30
    for i in range(len(lst)-1, -1, -1):
        cost = lst[i][1]
        if cost < cur:
            cur = cost
        suf[i] = cur
    starts_suf[length] = (ls, suf)

INF = 10**30
ans = INF

for l, r, c in segs:
    len1 = r - l + 1
    need = x - len1
    if need <= 0:
        continue
    # partner ending before this segment: ends_by_len[need] with r <= l-1
    if need in ends_pre:
        rs, pref = ends_pre[need]
        idx = bisect_right(rs, l-1) - 1
        if idx >= 0:
            cand = pref[idx] + c
            if cand < ans:
                ans = cand
    # partner starting after this segment: starts_by_len[need] with l >= r+1
    if need in starts_suf:
        ls, suf = starts_suf[need]
        idx = bisect_left(ls, r+1)
        if idx < len(ls):
            cand = suf[idx] + c
            if cand < ans:
                ans = cand

if ans >= INF:
    print(-1)
else:
    print(ans)
