import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
n = int(next(it)); x = int(next(it))
vouchers = []
MAXD = 2 * 10**5 + 5
for _ in range(n):
    l = int(next(it)); r = int(next(it)); c = int(next(it))
    d = r - l + 1
    vouchers.append((l, r, d, c))
# For each duration d, we want for any position to know minimal cost of interval of length d ending at or before pos,
# and starting at or after pos. We'll build arrays best_end[d][pos] = minimal cost among intervals length d with r == pos,
# then prefix min to get ending at or before pos. Similarly best_start[d][pos] for l == pos then suffix min.
# But durations up to MAXD; sparse. Use dict of arrays only for durations that appear.
from collections import defaultdict

by_d_end = defaultdict(lambda: [10**30] * (MAXD))  # index by r
by_d_start = defaultdict(lambda: [10**30] * (MAXD))  # index by l

durations_present = set()
for l, r, d, c in vouchers:
    durations_present.add(d)
    arr_end = by_d_end[d]
    if c < arr_end[r]:
        arr_end[r] = c
    arr_start = by_d_start[d]
    if c < arr_start[l]:
        arr_start[l] = c

# Build prefix mins for end arrays (positions 0..MAXD-1)
for d in list(durations_present):
    arr = by_d_end[d]
    for i in range(1, MAXD):
        if arr[i-1] < arr[i]:
            arr[i] = arr[i-1]
# Build suffix mins for start arrays
for d in list(durations_present):
    arr = by_d_start[d]
    for i in range(MAXD-2, -1, -1):
        if arr[i+1] < arr[i]:
            arr[i] = arr[i+1]

INF = 10**30
ans = INF
for l, r, d, c in vouchers:
    need = x - d
    if need <= 0:
        continue
    # try partner that ends before l: duration need, max r' < l => use by_d_end[need][l-1]
    if need in by_d_end:
        arr_end = by_d_end[need]
        if l-1 >= 0:
            cost1 = arr_end[l-1]
            if cost1 < INF:
                total = cost1 + c
                if total < ans:
                    ans = total
    # try partner that starts after r: duration need, min l' > r => by_d_start[need][r+1]
    if need in by_d_start:
        arr_start = by_d_start[need]
        if r+1 < MAXD:
            cost2 = arr_start[r+1]
            if cost2 < INF:
                total = cost2 + c
                if total < ans:
                    ans = total

print(-1 if ans == INF else ans)

