import sys

data = sys.stdin.read().strip().split()
if not data:
    sys.exit(0)
it = iter(data)
n = int(next(it)); x = int(next(it))
vouchers = []
MAXT = 200000
for _ in range(n):
    l = int(next(it)); r = int(next(it)); c = int(next(it))
    d = r - l + 1
    vouchers.append((l, r, d, c))

# For each duration d, we want minimal cost for segments ending at or before day t, and starting at or after day t.
INF = 10**30
# prepare arrays sized MAXT+3
best_end = [INF] * (MAXT + 3)  # best cost for segments of current duration that end at day t
best_start = [INF] * (MAXT + 3)  # best cost for segments of current duration that start at day t
# group vouchers by duration for processing faster
from collections import defaultdict
by_d = defaultdict(list)
for l, r, d, c in vouchers:
    by_d[d].append((l, r, c))

ans = INF
# For each possible duration d present, process pairs d and x-d
for d, segs in by_d.items():
    need = x - d
    if need <= 0:
        continue
    # build best_end for duration d
    # reset arrays only where used to avoid O(MAXT * many)
    # We'll fill arrays fresh for this d
    for i in range(MAXT + 3):
        best_end[i] = INF
        best_start[i] = INF
    for l, r, c in segs:
        if c < best_end[r]:
            best_end[r] = c
        if c < best_start[l]:
            best_start[l] = c
    # prefix minima for ends: best cost ending at or before i
    for i in range(1, MAXT + 3):
        if best_end[i-1] < best_end[i]:
            best_end[i] = best_end[i-1]
    # suffix minima for starts: best cost starting at or after i
    for i in range(MAXT + 1, -1, -1):
        if best_start[i+1] < best_start[i]:
            best_start[i] = best_start[i+1]
    # Now for segments of duration need, iterate their segments and try pair with d segments
    if need not in by_d:
        continue
    for l2, r2, c2 in by_d[need]:
        # try pair where need segment is to left: ends before d segment starts
        # so we need best_end[l2-1]
        if l2 - 1 >= 0:
            b = best_end[l2 - 1]
            if b < INF:
                total = b + c2
                if total < ans:
                    ans = total
        # try pair where need segment is to right: starts after r2
        if r2 + 1 <= MAXT + 2:
            b = best_start[r2 + 1]
            if b < INF:
                total = b + c2
                if total < ans:
                    ans = total

print(-1 if ans >= INF else ans)

