ns = [int(next(it)) for _ in range(t)]

def geo_sum_k_m(k, m, limit):
    # compute sum_{i=0..m} k^i, but stop and return >limit if exceeds
    total = 0
    term = 1
    for _ in range(m+1):
        total += term
        if total > limit:
            return limit + 1
        # next term
        if _ != m:
            # check multiplication overflow relative to limit
            if term > limit // k:
                term = limit + 1
            else:
                term *= k
    return total

out_lines = []
for n in ns:
    if n == 1:
        out_lines.append("NO")
        continue
    found = False
    # m is max power exponent in sum (sum 0..m), and problem requires at least two steps -> m >= 2
    # upper bound for m is such that 2^m <= n -> m <= 60
    for m in range(2, 61):
        # possible k in [2, floor(n^(1/m))]
        # if k^m > n then sum >= k^m > n so we can limit upper bound
