import sys
import math

def is_power(x, k):
    # check if x is integer power >=1 of k
    while x % k == 0:
        x //= k
    return x == 1

def main():
    data = sys.stdin.read().strip().split()
    t = int(data[0])
    res = []
    for i in range(1, t+1):
        n = int(data[i])
        ok = False
        if n < 7:
            res.append("NO")
            continue
        # Try k from 2 up to maybe 1e6; but we can stop when 1 + k + k*(k-1) > n minimal m=2
        # We iterate k while 1 + k + k*(k-1) <= n -> 1 + k^2 <= n -> k <= sqrt(n-1)
        limit = int(math.isqrt(n-1)) + 2
        for k in range(2, limit+1):
            total = 1
            cur = 1
            m = 0
            # generate until total >= n
            while total < n:
                if m == 0:
                    cur *= k
                else:
                    cur *= (k-1)
                total += cur
                m += 1
                if total == n and m >= 2:
                    ok = True
                    break
                if cur > n:
                    break
            if ok:
                break
        # Also for k > limit, sequences will be of form 1+k+k*(k-1)+... but m small.
        # For m=2: n = 1 + k + k*(k-1) = 1 + k^2 -> check if n-1 is perfect square >1
        if not ok:
            s = n - 1
            r = int(math.isqrt(s))
            if r*r == s and r > 1:
                ok = True
        res.append("YES" if ok else "NO")
    print("\n".join(res))

if __name__ == "__main__":
    main()

