import sys

def is_valid_befunge(program):
    # Simulate a basic Befunge interpreter for validation
    # This is a simplified version and may not cover all edge cases
    # For the purpose of this problem, we assume that the program is valid if it doesn't crash
    # due to stack underflow or other errors
    stack = []
    ip_x = 0
    ip_y = 0
    width = len(program[0]) if program else 0
    height = len(program)

    while True:
        if ip_y >= height or ip_y < 0 or ip_x >= width or ip_x < 0:
            return False  # Out of bounds
        c = program[ip_y][ip_x]
        if c.isdigit():
            stack.append(int(c))
        elif c == '+':
            if len(stack) < 2:
                return False
            a = stack.pop()
            b = stack.pop()
            stack.append(a + b)
        elif c == '-':
            if len(stack) < 2:
                return False
            a = stack.pop()
            b = stack.pop()
            stack.append(b - a)
        elif c == '*':
            if len(stack) < 2:
                return False
            a = stack.pop()
            b = stack.pop()
            stack.append(a * b)
        elif c == '/':
            if len(stack) < 2:
                return False
            a = stack.pop()
            if a == 0:
                return False
            b = stack.pop()
            stack.append(b // a)
        elif c == '%':
            if len(stack) < 2:
                return False
            a = stack.pop()
            if a == 0:
                return False
            b = stack.pop()
            stack.append(b % a)
        elif c == '!':
            if len(stack) < 1:
                return False
            stack.append(0 if stack.pop() != 0 else 1)
        elif c == '`':
            if len(stack) < 2:
                return False
            a = stack.pop()
            b = stack.pop()
            stack.append(1 if b > a else 0)
        elif c == '>':
            ip_x += 1
        elif c == '<':
            ip_x -= 1
        elif c == '^':
            ip_y -= 1
        elif c == 'v':
            ip_y += 1
