            # String mode (simplified to ignore)
            pass
        elif c == ':':
            if len(stack) < 1:
                return False
            stack.append(stack[-1])
        elif c == '\\':
            if len(stack) < 2:
                return False
            a = stack.pop()
            b = stack.pop()
            stack.append(a)
            stack.append(b)
        elif c == '$':
            if len(stack) < 1:
                return False
            stack.pop()
        elif c == '.':
            if len(stack) < 1:
                return False
            stack.pop()
        elif c == ',':
            if len(stack) < 1:
                return False
            stack.pop()
        elif c == '#':
            ip_x += 2
        elif c == 'g':
            if len(stack) < 2:
                return False
            y = stack.pop()
            x = stack.pop()
            if y >= height or y < 0 or x >= width or x < 0:
                return False
            stack.append(ord(program[y][x]))
        elif c == 'p':
            if len(stack) < 3:
                return False
            val = stack.pop()
            y = stack.pop()
            x = stack.pop()
            if y >= height or y < 0 or x >= width or x < 0:
                return False
            # Simplified, don't modify the program
        elif c == '&':
            # Input (simplified to push 0)
            stack.append(0)
        elif c == '~':
            # Input (simplified to push 0)
            stack.append(0)
        elif c == '@':
            return True  # End of program
        else:
            # Ignore other characters
            pass
        # Move to next instruction
        ip_x += 1

def generate_lex_smallest(program_str):
    # Extract all underscores from the program string
    underscores = [i for i, c in enumerate(program_str) if c == '_']
    n = len(underscores)
    if n == 0:
        return "0" if is_valid_befunge([list(program_str)]) else "false"

    # Replace underscores with digits in the order they appear
    # Try lex smallest first
    for digits in product('0123456789', repeat=n):
        temp_program = list(program_str)
        for i, pos in enumerate(underscores):
            temp_program[pos] = digits[i]
        # Convert to 2D array (for simulation)
        # For simplicity, treat it as a single row
        program = [temp_program]
        if is_valid_befunge(program):
            return ''.join(digits)
    return "false"

def main():
    import sys
    from itertools import product
    input_str = sys.stdin.read().strip()
    result = generate_lex_smallest(input_str)
    print(result)

if __name__ == "__main__":
    main()
