import sys
import math

MOD = 10**9 + 7

def comb(n, k, fact, inv_fact):
    if k < 0 or k > n:
        return 0
    return fact[n] * inv_fact[k] % MOD * inv_fact[n - k] % MOD

def main():
    input = sys.stdin.read
    data = input().split()

    max_nm = 2 * 10**5 + 10
    fact = [1] * max_nm
    for i in range(1, max_nm):
        fact[i] = fact[i-1] * i % MOD

    inv_fact = [1] * max_nm
    inv_fact[max_nm-1] = pow(fact[max_nm-1], MOD-2, MOD)
    for i in range(max_nm-2, -1, -1):
        inv_fact[i] = inv_fact[i+1] * (i+1) % MOD

    for line in data:
        n_str, m_str, g_str = line.split()
        n = int(n_str)
        m = int(m_str)
        g = int(g_str)

        total = n + m
        if total == 0:
            print(0)
            continue

        # We need to reduce the string to a single character g
        # Each operation reduces the length by 1
        # So, we need total - 1 operations to get to length 1
        # Each operation reduces the number of 0s and 1s by 1 (since two chars are replaced by one)
        # So, after total - 1 operations, we must have exactly 1 char left: g
        # Therefore, the number of 0s and 1s must differ by exactly 1
        if abs(n - m) != 1:
            print(0)
            continue

        if g == 0:
            if n == m + 1:
                print(comb(n + m, n, fact, inv_fact))
            else:
                print(0)
        else:
            if m == n + 1:
                print(comb(n + m, n, fact, inv_fact))
            else:
                print(0)

if __name__ == "__main__":
    main()
