import sys
import math

def main():
    import sys
    input = sys.stdin.read
    n, mod = map(int, input().split())

    # Precompute number of inversions for all permutations
    # We will use dynamic programming to count permutations with a certain number of inversions

    # dp[i][j] = number of permutations of length i with j inversions
    max_inversions = n * (n - 1) // 2
    dp = [[0] * (max_inversions + 1) for _ in range(n + 1)]
    dp[1][0] = 1

    for i in range(2, n + 1):
        for j in range(max_inversions + 1):
            for k in range(j + 1):
                if dp[i - 1][k]:
                    dp[i][j] += dp[i - 1][k]
                    dp[i][j] %= mod

    # Count total number of permutation pairs (p, q) where p < q lex and inv(p) > inv(q)
    total = 0
    inv_count = [0] * (max_inversions + 1)

    # Precompute the number of permutations with each inversion count
    for i in range(n + 1):
        for j in range(max_inversions + 1):
            inv_count[j] += dp[i][j]

    # Now compute the answer
    for i in range(n + 1):
        for j in range(i + 1, n + 1):
            for inv_p in range(max_inversions + 1):
                for inv_q in range(inv_p + 1):
                    if inv_count[inv_p] and inv_count[inv_q]:
                        if inv_p > inv_q:
                            total += inv_count[inv_p] * inv_count[inv_q]
                            total %= mod

    # Since we counted (p, q) and (q, p), and we want only p < q lex
    # The total number of permutation pairs is (n!)^2 / 2
    total_permutations = math.factorial(n)
    total_pairs = (total_permutations * total_permutations) // 2
    total_pairs %= mod

    # Subtract the number of pairs where inv(p) <= inv(q)
    # So we need to compute sum_{i,j} dp[i][a] * dp[j][b] where a <= b
    # But we already have the total number of pairs with inv(p) > inv(q)
    # So our answer is total

    print(total % mod)

if __name__ == "__main__":
    main()
