import sys

def main():
    n = int(sys.stdin.readline())
    a = list(map(int, sys.stdin.readline().split()))

    max_len = 1
    current_len = 1
    prev = a[0]

    for i in range(1, n):
        if a[i] > prev:
            current_len += 1
            prev = a[i]
        else:
            # We can change one element to make the sequence strictly increasing
            # So we check if the previous sequence can be extended by changing a[i] or a[i-1]
            if i == 1:
                # First two elements, can always be made strictly increasing by changing one
                current_len = 2
            else:
                # Check if a[i] > a[i-2], then we can change a[i-1]
                if a[i] > a[i-2]:
                    current_len = i - 1 + 1  # from a[i-2] to a[i]
                else:
                    # Otherwise, reset the current length to 1 or 2 depending on a[i] and a[i-1]
                    current_len = 2 if a[i] > a[i-1] else 1
            prev = a[i]

        max_len = max(max_len, current_len)

    print(max_len)

if __name__ == "__main__":
    main()
