import sys

def main():
    for line in sys.stdin:
        s = line.strip()
        n = len(s)
        found = False
        for L in range(n-1, 0, -1):
            seen = set()
            for i in range(n - L + 1):
                substr = s[i:i+L]
                if substr in seen:
                    print(L)
                    found = True
                    break
                seen.add(substr)
            if found:
                break
        if not found:
            print(0)

if __name__ == "__main__":
    main()
