s = input().strip()

fixed = []
ones = []
for c in s:
    if c == '1':
        ones.append(c)
    else:
        fixed.append(c)

if not fixed:
    print(''.join(ones))
elif not ones:
    print(''.join(fixed))
else:
    if '2' not in fixed:
        # All 0s, append ones
        print(''.join(fixed + ones))
    elif '0' not in fixed:
        # All 2s, prepend ones
        print(''.join(ones + fixed))
    else:
        first_2 = fixed.index('2')
        result = fixed[:first_2] + ones + fixed[first_2:]
        print(''.join(result))
