def minimum_ternary_string(s):
    zeros = []
    ones = []
    twos = []

    for char in s:
        if char == '0':
            zeros.append(char)
        elif char == '1':
            ones.append(char)
        else:
            twos.append(char)

    result = zeros + ones + twos
    return ''.join(result)

