import sys
import math

def is_snowflake(n):
    if n == 1:
        return False
    # Check if n can be represented as (k^m - 1) / (k - 1)
    # We iterate over possible m from 2 to log2(n) + 1
    max_m = int(math.log(n, 2)) + 2
    for m in range(2, max_m + 1):
        # Try all possible k > 1 such that (k^m - 1) // (k - 1) == n
        # Binary search for k
        low = 2
        high = n
        while low <= high:
            mid = (low + high) // 2
            # Compute (mid^m - 1) // (mid - 1)
            total = 0
            power = 1
            for _ in range(m):
                total += power
                power *= mid
            if total == n:
                return True
            elif total < n:
                low = mid + 1
            else:
                high = mid - 1
    return False

def main():
    input = sys.stdin.read
    data = input().split()
    t = int(data[0])
    results = []
    for i in range(1, t + 1):
        n = int(data[i])
        if is_snowflake(n):
            results.append("YES")
        else:
            results.append("NO")
    print('\n'.join(results))

if __name__ == "__main__":
    main()
