import sys
from collections import defaultdict

def main():
    n = int(sys.stdin.readline())
    strings = [sys.stdin.readline().strip() for _ in range(2 * n - 2)]

    # Group strings by their length
    length_map = defaultdict(list)
    for s in strings:
        length_map[len(s)].append(s)

    # Try all possible combinations of prefixes and suffixes
    from itertools import product

    # Get all possible prefix and suffix sets
    for prefix_set in product([0, 1], repeat=len(strings)):
        prefix_indices = [i for i, val in enumerate(prefix_set) if val == 1]
        suffix_indices = [i for i, val in enumerate(prefix_set) if val == 0]

        # Check if the number of 'P' and 'S' is equal
        if len(prefix_indices) != len(suffix_indices):
            continue

        # Extract prefix and suffix strings
        prefixes = [strings[i] for i in prefix_indices]
        suffixes = [strings[i] for i in suffix_indices]

        # Check if there's a string that matches all prefixes and suffixes
        found = False
        for candidate in set(prefixes + suffixes):
            if len(candidate) != n:
                continue

            valid = True
            for p in prefixes:
                if not candidate.startswith(p):
                    valid = False
                    break
            if not valid:
                continue

            for s in suffixes:
                if not candidate.endswith(s):
                    valid = False
                    break
            if valid:
                found = True
                break

        if found:
            result = ['P' if i in prefix_indices else 'S' for i in range(2 * n - 2)]
            print(''.join(result))
            return

if __name__ == "__main__":
    main()
