import sys
import functools

def main():
    import sys
    input = sys.stdin.read
    data = input().split()
    n = int(data[0])
    A = data[1]

    @functools.lru_cache(maxsize=None)
    def dp(pos, h_count, m_count, h_num, m_num):
        if pos == 2 * n:
            return (h_num, m_num, "")

        digit = A[pos]
        best_val = -1
        best_seq = ""

        # Homer's turn
        if h_count < n:
            new_h_num = h_num * 10 + int(digit)
            res, _, seq = dp(pos + 1, h_count + 1, m_count, new_h_num, m_num)
            total = res
            if total > best_val:
                best_val = total
                best_seq = "H" + seq

        # Marge's turn
        if m_count < n:
            new_m_num = m_num * 10 + int(digit)
            res, _, seq = dp(pos + 1, h_count, m_count + 1, h_num, new_m_num)
            total = res
            if total > best_val:
                best_val = total
                best_seq = "M" + seq

        return (best_val, h_num, m_num, best_seq)

    max_total, _, _, result = dp(0, 0, 0, 0, 0)
    print(result)

if __name__ == "__main__":
    main()
