        return
    n = int(data[0])
    mod = int(data[1])

    if n == 0:
        print(0)
        return
    if n == 1:
        print(0)
        return

    fact = [1] * (n + 1)
    for i in range(1, n + 1):
        fact[i] = fact[i - 1] * i % mod

    total_perm = fact[n]
    total_pairs = total_perm * total_perm % mod

    dp = [0] * (n + 1)
    dp[0] = 0

