import sys

def main():
    data = sys.stdin.read().splitlines()
    results = []
    for i in range(0, len(data), 3):
        if i + 2 >= len(data):
            break
        guest = data[i].strip()
        host = data[i+1].strip()
        pile = data[i+2].strip()
        combined = guest + host
        if sorted(combined) == sorted(pile):
            results.append("YES")
        else:
            results.append("NO")

    for res in results:
        print(res)

if __name__ == "__main__":
    main()
