import sys

mod = 998244353

def main():
    data = sys.stdin.read().split()
    if not data:
        return

    t = int(data[0])
    index = 1
    results = []

    for _ in range(t):
        N = int(data[index]); index += 1
        K = int(data[index]); index += 1

        if N == 0:
            results.append("1")
            continue

        if K == 0:
            if N == 1:
                results.append("1")
            else:
                results.append("0")
            continue

        if N == 1:
            ans = pow(2, K, mod)
            results.append(str(ans))
            continue

        dp = [0] * (K + 1)
        dp[0] = 1

        for i in range(1, K + 1):
            new_dp = [0] * (K + 1)
            for j in range(K + 1):
                if j >= i:
                    new_dp[j] = (new_dp[j] + dp[j]) % mod
                if j + 1 <= K:
                    new_dp[j + 1] = (new_dp[j + 1] + dp[j] * i) % mod
            dp = new_dp

        total = 0
        for i in range(K + 1):
            total = (total + dp[i]) % mod

        results.append(str(total))

    print("\n".join(results))

if __name__ == "__main__":
    main()
