MOD = 10**9 + 7

def main():
    import sys
    data = sys.stdin.read().splitlines()
    m = int(data[0])
    s_list = []
    for i in range(1, m + 1):
        s_list.append(data[i].strip())

    dp = [0] * (m + 1)
    ans = []
    total_sequences = 0

    for i in range(1, m + 1):
        char = s_list[i - 1]
        new_char = char

        if i >= 1:
            dp[i] = dp[i - 1]

        if i >= 2:
            two_char = new_char + s_list[i - 2]
            dp[i] = (dp[i] + dp[i - 2]) % MOD

        if i >= 3:
            three_char = new_char + s_list[i - 1:i - 1 + 2]
            dp[i] = (dp[i] + dp[i - 3]) % MOD

